mapper.insert:

    SELECT LAST_INSERT_ID()

insert into tbl_dept (
  upper_dept_no
, company_no
, dept_name
, dept_desc
, depth
, reg_user)
values (

null


#{upperDeptNo}

, #{companyNo}
, #{deptName}
, #{deptDesc}

, 1


, (
select d2.depth+1 as depth
from tbl_dept d2
where dept_no=#{upperDeptNo}
)

, #{username}
)
;

mapper.select:
select *
from tbl_dept
where (upper_dept_no = #{upperDeptNo} OR upper_dept_no is null )and depth = #{depth}
