mapper.insert:
INSERT INTO tbl_orga (
    upper_orga_no, orga_type, reg_user
)
VALUES (
    (
        SELECT tbl_dept_mapping.orga_no
        FROM tbl_dept_mapping
        JOIN tbl_dept td ON td.dept_no = tbl_dept_mapping.dept_no
        WHERE td.dept_no = #{deptNo}
    ),
    'emp', #{regUser}
)

    SELECT LAST_INSERT_ID()

mapper.update:
UPDATE  tbl_emp SET
                    last_login_time = #{lastLoginTime}, last_login_ip = #{lastLoginIp}
WHERE emp_no = #{empNo}

UPDATE tbl_emp
SET uuid = #{uuid}, filename = #{filename}
WHERE emp_no = #{empNo};
