mapper.select:
select mr.* from tbl_menu m join (select tm.* from tbl_menu tm join (select tmr.role_group_no, tm.menu_no from tbl_menu_role tmr join tbl_menu tm on tmr.menu_no = tm.menu_no where tmr.role_group_no in
#{item}
group by tmr.role_group_no, tm.menu_no) rg on rg.menu_no = tm.menu_no where tm.main_flag = true) mr on mr.menu_no = m.menu_no
where m.upper_menu_no IS NULL
where m.upper_menu_no = #{menuNo}
