mapper.select:
select mygroup_no, mygroup_name
from tbl_mygroup
where mygroup_no=#{mygroupNo}
;

mapper.update:
update tbl_mygroup set mygroup_name=#{mygroupName}
                     , mod_user=(select emp_name
                                 from tbl_emp
                                 where username=#{username})
                     , mod_date=now()
where mygroup_no=#{mygroupNo}
;
