mapper.select:
select temp1.menu_no as menu_no, menu_name, IF(temp1.menu_no = temp2.menu_no, true, false) checked from (with recursive menuAll as (select m1.menu_name, m1.menu_no, m1.upper_menu_no from tbl_menu m1 where m1.menu_code = #{moduleCode}
union all
select m3.menu_name, m3.menu_no, m3.upper_menu_no from menuAll m2 join tbl_menu m3 on m3.upper_menu_no = m2.menu_no) select ma.menu_no, ma.menu_name from menuAll ma) temp1 left outer join (select menu_no from tbl_menu_role where role_group_no = #{roleGroupNo}) temp2 on temp1.menu_no = temp2.menu_no
